import React from "react";
import Img from '../user.jpg'

const Message = ({ msg }) => {
    return (
        <div className="message_wrapper">
            <p>
                {msg.media ? <img src={msg.media} alt={msg.text} /> : null}
            </p>
        </div>
    )
}

export default Message;
